    // Función para limpiar los campos después de registrar al alumno
    private fun limpiarCampos() {
        etNombre.text.clear()
        etApellido.text.clear()
        etCorreo.text.clear()
        etContraseña.text.clear()
        etTelefono.text.clear()
    }
